import '../index.css';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import { useState, useEffect } from 'react';
import ImagePopup from './ImagePopup';
import { api } from '../utils/api';
import { CurrentUserContext } from '../context/CurrentUserContext';
import EditProfilePopup from './EditProfilePopup';


function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isBinPopupOpen, setIsBinPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);
  const [removeCard, setRemoveCard] = useState(null);

  // delete card 



  // add & delete like on card
  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.like(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
    api.dislike(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
  }
  useEffect(() => {
    api.getMe()
      .then((userInfo) => {
        setCurrentUser(userInfo);
      })
  }, []);

  useEffect(() => {
    api.getCards()
      .then((cards) => {
        setCards(cards);
      })
  }, []);


  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleUserUpdate(data) {
api.setMe(data)
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsBinPopupOpen(false);
    setSelectedCard(null);
  }

  function handleBinPopupClick(card) {
    setIsBinPopupOpen(true);
    setRemoveCard(card);
  }

  function handleCardDelete(card) {
    api.removeCard(card._id)
      .then(() => {
        setRemoveCard(card);
        setCards((newArray) => newArray.filter((item) => card._id !== item._id));
        closeAllPopups();
      })
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header />
        <Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onCardClick={handleCardClick}
          onBinClick={handleBinPopupClick}
          cards={cards}
          onCardLike={handleCardLike}
        />
        <Footer />
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUserUpdate={handleUserUpdate}
        />
        <PopupWithForm
          name='add-popup'
          isOpen={isAddPlacePopupOpen}
          title='Новое место'
          onClose={closeAllPopups}
          saveText='Создать'
        >
          <input
            required
            minLength="2"
            maxLength="30"
            className="popup__popup-form popup__popup-form_text_place"
            id="form-place"
            name="name"
            type="text"
            placeholder="Название" />
          <span className="popup__error" id="form-place-error"></span>
          <input
            required
            className="popup__popup-form popup__popup-form_text_image"
            id="form-image"
            name="link"
            type="url"
            placeholder="Ссылка на картинку" />
          <span className="popup__error" id="form-image-error"></span>

        </PopupWithForm>

        <PopupWithForm
          name='avatar-popup'
          isOpen={isEditAvatarPopupOpen}
          title='Обновить аватар'
          onClose={closeAllPopups}
          saveText='Сохранить'
        >
          <input
            required
            className="popup__popup-form popup__popup-form_text_image"
            id="form-avatar"
            name="link"
            type="url"
            placeholder="Ссылка на картинку"
          />
          <span className="popup__error" id="form-avatar-error"></span>

        </PopupWithForm>

        <PopupWithForm
          name='bin-popup'
          isOpen={isBinPopupOpen}
          title='Вы уверены?'
          onClose={closeAllPopups}
          saveText='Да'
          onDeleteCard={handleCardDelete}
        />

        <ImagePopup
          isOpen={selectedCard}
          onClose={closeAllPopups}
          card={selectedCard}
        />

      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
