import logoMesto from '../images/logo.svg'
import { Route, Routes, Link } from "react-router-dom";
import { useState, useEffect } from 'react';


function Header({ email, onSignOut, loggedIn }) {

  return (
    <>
      {loggedIn &&
        <div>
          <div className="header">
            {email && (<p className={classEmail}>{email}</p>)}
            <Link
              to="../sign-in"
              className={classLink}
              onClick={onSignOut}
            >
              {"Выйти"}
            </Link>
          </div>
          <HeaderLink
            linkTo="../sign-in"
            textLink="Выйти"
            email={email}
            classContainer="header__container header__container_top"
            classEmail="header__link header__link_top header__email"
            classLink="header__link header__link_top header__link_exit"
            onSignOut={onSignOut}
          />
        </div>


      }

      <header className="header">
        <img className="header__logo" src={logoMesto} alt="Лого" />

        <Routes>
          <Route
            path="/sign-in"
            element={
              <HeaderLink
                linkTo="../sign-up"
                textLink="Регистрация"
                email={email}
                classLink="header__link"
              />
            }
          />

          <Route
            path="/sign-up"
            element={
              <HeaderLink
                linkTo="../sign-in"
                textLink="Войти"
                email={email}
                classLink="header__link"
              />
            }
          />

          <Route
            exact path="/"
            element={
              <>
                {currentWidth > 950 ?
                  <HeaderLink
                    linkTo="../sign-in"
                    textLink="Выйти"
                    email={email}
                    classContainer="header__container"
                    classEmail="header__link header__email"
                    classLink="header__link header__link_exit"
                    onSignOut={onSignOut}
                  />
                  :
                  <button
                    className={`header__nav-btn ${hamburgerOn && "header__nav-btn_close"}`}
                    type="button"
                    onClick={handleMenuBtnClick}
                  ></button>
                }
              </>
            }
          />
        </Routes>
      </header>
    </>
  );
}

export default Header
