import React from "react";
import SuccessIcon from "../images/SuccessIcon.svg";
import FailIcon from "../images/FailIcon.svg";


function InfoTooltip({ isOpen, onClose, isSuccessful, onClickPass }) {


    return (
        <div
          className={`popup popup_type_tooltip ${isOpen ? "popup_is-opened" : ""}`}
        >
          <div className="popup__content">
            {props.isSuccess ? (
              <>
                <img
                  src={`${SuccessIcon}`}
                  alt="Регистрация прошла успешно."
                  className="popup__tooltip_image"
                />
                <p className="popup__tooltip_message">
                  Вы успешно зарегистрировались!
                </p>
              </>
            ) : (
              <>
                <img
                  src={`${FailIcon}`}
                  alt="Регистрация не была выполнена."
                  className="popup__tooltip_image"
                />
                <p className="popup__tooltip_message">
                  Что-то пошло не так. Попробуйте ещё раз!
                </p>
              </>
            )}

            <button type="button" className="popup__close" onClick={props.onClose}>
              <img src={closeIcon} alt="кнопка закрытия попапа" />
            </button>
          </div>
        </div>
      );

  const title = isSuccessful ? "Вы успешно зарегистрировались!" : "Что-то пошло не так! Попробуйте ещё раз.";
  const iconAlarm = isSuccessful ? "popup__icon_successfull" : "popup__icon_unsuccessfull";

  return (
    <PopupWithForm
      name="tooltip"
      isOpen={isOpen}
      title={title}
      onClose={onClose}
      buttonText=""
      onSubmit={null}

      isBtnSubmit={false}
      ariaLabel=""

      isEnabled={true}
      iconAlarm={iconAlarm}
      onClickPass={onClickPass}
    />
  );
};

export default InfoTooltip;
