import closeIcon from "../images/Close-Icon.svg";


function InfoTooltip({ isOpen, onClose, isSuccessful }) {

    const title = isSuccessful ? "Вы успешно зарегистрировались!" : "Что-то пошло не так! Попробуйте ещё раз.";
    const iconAlarm = isSuccessful ? "popup__icon_successfull" : "popup__icon_unsuccessfull";
    return (
        <section className={`popup  ${isOpen ? 'popup_opened' : ''}`} >
            <div className='popup__container' >
                {iconAlarm &&
                    <div className={`popup__tooltip_image ${iconAlarm}`} />
                }
                <button onClick={onClose}
                    className="popup__close-button"
                    type="button"></button>
                <h3 className="popup__title">{title}</h3>
            </div>
        </section>
    );
